     .moon{
        animation: moon_anim 4s ease-in-out infinite;
     }
     @keyframes moon_anim {
        0%{transform: rotate(0deg); transform-origin: 75% 25%;}
        50%{transform: rotate(15deg); transform-origin: 75% 25%;}
        100%{transform: rotate(0deg); transform-origin: 75% 25%;}
     }

